import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from '../assets/css/FindId.module.css'

const SignUp = ({requestPost}) => {
    const [check, setCheck] = useState(true);
    const [state, setState] = useState({
        id: '',
        domain: '',
        email: '',
        password: '',
        confirmPasswd: '',
        name: '',
        phone_number: '',
        birthDate: '',
    });
    //const [checkbox, setCheckbox] = useState();

    const handleChangeState = (e) => {
        const target = e.target;
        setState(
            {
                ...state,
                [target.name]: target.value,
            }
        );
    }

    const handleDomainSelect = (e) => {
        const target = e.target;
        setState(
            {
                ...state,
                domain: target.value,
            }
        );
    }

    const handleSignUp = () => {
       checkBlank(); //빈칸 체크
       checkPassword(); //비밀번호 확인 체크
       if(!check) return;
       if(check){        
        console.log(state.id);
        console.log(state.email);
        const url = '/users/register';
        requestPost(url, state);
      }
    }

    const checkPassword = () => {
        const pw = state.password;
        const confirmPw = state.confirmPasswd;
        if(pw === '' || pw !== confirmPw){
            alert('비밀번호가 일치하지 않습니다.');
            setCheck(false);
        }
    }

    const checkBlank = () => {
      for(const key in state){
          if(state[key] === ""){
              alert('모든 칸을 채워주세요.');
              setCheck(false);
              break;
          }
      }
  }

    useEffect(()=>{
      setState({
          ...state,
          email: state.id+'@'+state.domain
      })
      },[state.id, state.domain])


  return (
    <section className={styles.findId}>
      <h1 className={styles.srOnly}>아이디</h1>
      <div className={styles.container}>
        <label 
          className={styles.label}
        >
            아이디
        </label>
        <div>
        <input
          className={styles.input}
          type="text" id="userId"
          name="id"
          value={state.id}
          onChange={handleChangeState}
        />
        <span> @ </span>
        <input
          className={styles.input}
          type="text" id="userDomain"
          name="domain"
          value={state.domain}
          onChange={handleChangeState}
        />
        <span>  </span>
        <select className={styles.input} name="language" onChange={handleDomainSelect}>
            <option value="">선택</option>
            <option value="naver.com">naver.com</option>
            <option value="daum.net">daum.net</option>
            <option value="hanmail.net">hanmail.net</option>
            <option value="nate.com">nate.com</option>
            <option value="gmail.com">gmail.com</option>
            <option value="hotmail.com">hotmail.com</option>
            <option value="hanmir.com">hanmir.com</option>
            <option value="dreamwiz.com">dreamwiz.com</option>
            <option value="lycos.co.kr">lycos.co.kr</option>
            <option value="empas.com">empas.com</option>
            <option value="">직접 입력</option>
        </select>
        </div>
        
        <label 
          className={styles.label}
        >
            비밀번호
        </label>
        <input
          className={styles.input}
          type="password"
          name="password"
          value={state.password}
          onChange={handleChangeState}
        />
        <label 
          className={styles.label}
        >
            비밀번호 확인
        </label>
        <input
          className={styles.input}
          type="password"
          name="confirmPasswd"
          value={state.confirmPasswd}
          onChange={handleChangeState}
        />
        <label 
          className={styles.label}
        >
            이름
        </label>
        <input
          className={styles.input}
          type="text"
          name="name"
          value={state.name}
          onChange={handleChangeState}
        />
        <label 
          className={styles.label}
        >
            연락처
        </label>
        <input
          className={styles.input}
          type="text"
          name="phone_number"
          value={state.phone_number}
          onChange={handleChangeState}
        />
        <label 
          className={styles.label}
        >
            생년월일
        </label>
        <input
          className={styles.input}
          type="text"
          name="birthDate"
          value={state.birthDate}
          onChange={handleChangeState}
        />
        <form>
        <input type='checkbox' name='all_checked' value='all_agree' />모두 동의합니다.
        <br></br>
        <input type='checkbox' name='age_checked' value='age_check' />(필수) 본인은 만 14세 미만이 아닙니다.
        <br></br>
        <input type='checkbox' name='agree_checked' value='agree_check' />(필수) 이용약관에 동의합니다.
        <br></br>
        <input type='checkbox' name='personal_checked' value='personal_check' />(필수) 개인정보처리방침에 동의합니다.
        <br></br>
        <input type='checkbox' name='marketing_checked' value='marketing_agree' />(선택) 시향하다에서 제공하는 소식을 SMS, 이메일로 수신하겠습니다.
        <br></br>
        </form>
        <br></br>
        <button 
          type="button"
          className={styles.button}
          onClick={handleSignUp}
        >
          회원가입
        </button>
      </div>
    </section>
  );
}

export default SignUp
